function TCRSDepartment(c,d){this.crsDataSet=c;TCRSDataRecord.call(this,this.crsDataSet,"department","DEPT_ID,DEPT_NAME,MANAGER,LEADER1,LEADER2","DEPT_ID='"+d+"'","")}TCRSDepartment.prototype=new TCRSDataRecord();TCRSDepartment.prototype.get_name_by_id=function(d){this.loadData("#tabledepartment");var c=this.search("DEPT_ID",d);if(c==null){var e=this.crsDataSet.get_dept_by_id(d);if(e.length>0){var f=this;$.each(e,function(a,b){f.rows.push(b)});c=e[0]}}if(c!=null){return c.DEPT_NAME}return d};TCRSDepartment.prototype.get_id_by_name=function(d){this.loadData("#tabledepartment");var c=this.search("DEPT_NAME",d);if(c==null){var e=this.crsDataSet.get_dept_by_name(d);if(e.length>0){var f=this;$.each(e,function(a,b){f.rows.push(b)});c=e[0]}}if(c!=null){return c.DEPT_ID}return d};TCRSDepartment.prototype.get_name_list_by_id=function(h){if(h!=""){var e=h.split(",");h="";for(var g=0,f=e.length;g<f;g++){if(e[g]!=""){h+=this.get_name_by_id(e[g])+","}}}return h};TCRSDepartment.prototype.get_id_list_by_name=function(h){if(h!=""){var e=h.split(",");h="";for(var g=0,f=e.length;g<f;g++){if(e[g]!=""){h+=this.get_id_by_name(e[g])+","}}}return h};TCRSDepartment.prototype.is_dept_manager=function(k,i,m){if(m!=""){var o=this.search("DEPT_ID",i);if(o!=null){var p=m.split(",");for(var n=0,j=p.length-1;n<j;n++){var l="";switch(p[n]){case"\u90e8\u95e8\u4e3b\u7ba1":l=o.MANAGER;break;case"\u4e0a\u7ea7\u4e3b\u7ba1\u9886\u5bfc":l=o.LEADER1;break;case"\u4e0a\u7ea7\u5206\u7ba1\u9886\u5bfc":l=o.LEADER2;break}if(l!=""){if((","+l).indexOf(","+k+",")!=-1){return true}}}}}return false};
